import React from 'react'
import Modal from 'react-modal'
import './Modal.css'

Modal.setAppElement(document.getElementById('root'));

const ListModal = (props) => {

   const filterDebts = (debts) => {
      return props.debts.filter((item) => item.name === props.listingSomeonesDebts)
   }

   return (
      <Modal
         className='list-modal'
         isOpen={!!props.listingSomeonesDebts}
         onRequestClose={props.handleClearListingSomeonesDebts}
         contentLabel="DÃ­vidas do Cliente"
      >
         <h3>{props.listingSomeonesDebts ? props.listingSomeonesDebts : ''}</h3>
         <div>
            {
               filterDebts().map((element, index) => (
                  <div key={index}>{element.description}</div>
               ))
            }
         </div>
         <button onClick={props.handleClearListingSomeonesDebts}>Ok</button>
      </Modal>
   )
}

export default ListModal
